mapper.select:

		   select * from (
			        select rownum rn, TMP.* from (
			        select * from zip_code
			        WHERE instr(sido, #{keyword})>0 
        			OR instr(sigungu, #{keyword})>0 
        			OR instr(eupmyun, #{keyword})>0 
        			OR instr(hdong_name, #{keyword})>0
			        ) TMP
			    )
			    where rn between #{startRow} and #{finishRow}
